import { useState } from "react";

function Todo(props) {
  useState(false);

  function deleteHandler() {
    this.state = true;
  }

  return (
    <div className={"card"}>
      <h2>{props.text}</h2>
      <div className={"actions"}>
        <button className={"btn"} onClick={deleteHandler}>
          Delete
        </button>
      </div>
    </div>
  );
}

export default Todo;
